// Helper function to get Ganpati details by name
Map<String, dynamic>? getGanpatiDetails(String name) {
  return ganpatiDetails[name];
}

// Helper function to get all Ganpati names
List<String> getAllGanpatiNames() {
  return ganpatiDetails.keys.toList();
}

// Helper function to get Ganpati image widget
ImageProvider? getGanpatiImage(String name, {ImageProvider? defaultImage}) {
  final details = ganpatiDetails[name];
  if (details != null && details['image'] != null) {
    try {
      return AssetImage(details['image']);
    } catch (e) {
      return defaultImage;
    }
  }
  return defaultImage;
}

// Helper function to get Ganpati description
String getGanpatiDescription(String name, {String defaultDescription = ''}) {
  return ganpatiDetails[name]?['description'] ?? defaultDescription;
}